import React, { useState } from "react";

interface IProps {
  children: any;
  open: boolean;
}

export const Burger = () => {
  const [open, setOpen] = useState(false);

  return (
    <main className="burger-main" onClick={() => setOpen(!open)}>
      <div />
      <div />
      <div />
      <style jsx>
        {`
          .burger-main {
            width: 2rem;
            height: 2rem;
            position: fixed;
            top: 15px;
            right: 20px;
            display: flex;
            justify-content: space-around;
            flex-flow: column nowrap;
          }

          div {
            width: 2rem;
            height: 0.25rem;
            background-color: ${({ open }: IProps) => (open ? "#ccc" : "#333")};
          }
        `}
      </style>
    </main>
  );
};
